  /* 
  This funtion gets the data from the json docs to be printed
  My solution approach was to get all the json data for the following catergories:
  
  1 Introduction , 2 Geography - 3 People and Society, 4 Energy, 5 Communications.
  
  So as some country data can contain more information than the others 
  I included conditions to get access the sub lists and and exclude some words such as text, part to, among others.
  
  For a better visialition on the table
  
  */
  
  function getData($stringArray) {
    
    // variable to check the coutries/ stringArray size to pass the right json doc
    $rounds = count($stringArray);
    
    
    for ($country = 0; $country < $rounds;  $country++) {
       
      // loop through the array of selected categories returned by the checkboxes
      foreach($_GET['category'] as $el) {
        
        // variable to loop through the country array returned by the checkboxes 
        $area = $stringArray[$country];
        
        if ($stringArray[$country] == "Germany") {
          $coutryData = file_get_contents('gm.json');
        }
        if ($stringArray[$country] == "France") {
          $coutryData = file_get_contents('fr.json');
        }
        if ($stringArray[$country] == "Italy") {
          $coutryData = file_get_contents('it.json');
        }
        if ($stringArray[$country] == "UK") {
          $coutryData = file_get_contents('uk.json');
        }
        
        // Variable to decode the country json file of the round
        $result = json_decode($coutryData, true);
        
        // if it is equal the Intro add to the background array
        if ($el == "Introduction") {
          
          // Add category name and country with a 0 prefix as an indentifier for the print table function
          $backArray[] = 0 . "Introduction";
          $backArray[] = 0 . $area;
          
          foreach($result['Introduction'] as $key=>$value){
            
            // Add the key as a row element with the 1 prefix as an indentifier for the print table function 
            $backArray[] = 1 . $key;
            
            // iterate throught the value as key and value checking for sub lists
            foreach($value as $key=>$value) {
              
              // If value is not equal text means that it is the right result for the first coloumn
              if ($key != "text") {
                $backArray[] = 1 . $key;
              }
              else {
                // if it is text add the value with a 2 as an indentifier for the second coloumn
                $backArray[] = 2 . $value;
              }
              // go even deper in the jason array to get the right info for the 2 column
              foreach($value as $key=>$value) {
                
                if ($key != "text") {
                  $backArray[] = 2 . $key;
                }
                else {
                  $backArray[] = 2 . $value;
                }
                // And once more - That was the deepest that the sub lists could go for the 5 cetegories
                foreach($value as $key=>$value) {
                  if ($key != "text") {
                    $backArray[] = 2 . $key;
                  }
                  else {
                    $backArray[] = 2 . $value;
                  }
                }
              }
            }
          }
        }
        // Same principle for the other categories
        if ($el == "Geography") {
          
          $geoArray[] = 0 . "Geography";
          $geoArray[] = 0 . $area;
          
          foreach($result['Geography'] as $key=>$value) {
            
            $geoArray[] = 1 . $key;
            
            // Special case for the France data
            if ($stringArray[$country] == "France") {
              if ($key == "Location" || $key == "Geographic coordinates" || $key == "Map references" || $key == "Climate" || $key == "Terrain" || $key == "Natural resources" || $key == "Natural hazards") {
                
                // Add this info as a value for the above subject because France data has sub classes for them
                $geoArray[] = 2 . "See Sub Classes Below";
              }
            }
            foreach($value as $key=>$value) {
              if ($key != "text") {
                
                if ($key != "total" && $key != "party to" && $key != "territorial sea" && $key != "mean elevation" && $key != "agricultural land" && $key != "metropolitan France - total") {
                  $geoArray[] = 1 . $key;
                }
              }
              else {
                $geoArray[] = 2 . $value;
              }
              foreach($value as $key=>$value) {
                if ($key != "text") {
                  $geoArray[] = 2 . $key;
                }
                else {
                  $geoArray[] = 2 . $value;
                }
                foreach($value as $key=>$value) {
                  if ($key != "text") {
                    $geoArray[] = 2 . $key;
                  }
                  else {
                    $geoArray[] = 2 . $value;
                  }
                }
              }
            }
          }
        }
        if ($el == "Society") {
          $socArray[] = 0 . "Society";
          $socArray[] = 0 . $area;
          
          foreach($result['People and Society'] as $key=>$value){
            $socArray[] = 1 . $key;
            
            // Special case applies for all the countries in this case
            if ($key == "Age structure" || $key == "Sex ratio" || $key == "Sex ratio" || $key == "Life expectancy at birth" || $key == "Drinking water source" || $key == "Sanitation facility access" || $key == "Urbanization") {
              $socArray[] = 2 . "See Sub Classes Below";
            }
            foreach($value as $key=>$value) {
              if ($key != "text" ) {
                if ($key != "noun" && $key != "total" && $key != "total dependency ratio" && $key != "definition") {
                  $socArray[] = 1 . $key;
                }
              }
              else {
                $socArray[] = 2 . $value;
              }
              foreach($value as $key=>$value) {
                if ($key != "text") {
                  $socArray[] = 2 . $key;
                }
                else {
                  $socArray[] = 2 . $value;
                }
                foreach($value as $key=>$value) {
                  if ($key != "text") {
                    $socArray[] = 2 . $key;
                  }
                  else {
                    $socArray[] = 2 . $value;
                  }
                }
              }
            }
          }
        }
        if ($el == "Energy") {
          
          $enerArray[] = 0 . "Energy";
          $enerArray[] = 0 . $area;
          foreach($result['Energy'] as $key=>$value){
            
            $enerArray[] = 1 . $key;
            foreach($value as $key=>$value) {
              if ($key != "text") {
                $enerArray[] = 1 . $key;
              }
              else {
                $enerArray[] = 2 . $value;
              }
              foreach($value as $key=>$value) {
                if ($key != "text") {
                  $enerArray[] = 2 . $key;
                }
                else {
                  $enerArray[] = 2 . $value;
                }
                foreach($value as $key=>$value) {
                  if ($key != "text") {
                    $enerArray[] = 2 . $key;
                  }
                  else {
                    $enerArray[] = 2 . $value;
                  }
                }
              }
            }
          }
        }
        if ($el == "Communications") {
          
          $commArray[] = 0 . "Communications";
          $commArray[] = 0 . $area;
          
          foreach($result['Communications'] as $key=>$value){
            
            $commArray[] = 1 . $key;
            
            foreach($value as $key=>$value) {
              
              if ($key != "text") {
                if ($key != "total subscriptions" && $key != "total" && $key != "general assessment") {
                  $commArray[] = 1 . $key;
                }
              }
              
              else {
                $commArray[] = 2 . $value;
              }
              
              foreach($value as $key=>$value) {
                if ($key != "text") {
                  $commArray[] = 2 . $key;
                }
                
                else {
                  $commArray[] = 2 . $value;
                }
                foreach($value as $key=>$value) {
                  if ($key != "text") {
                    $commArray[] = 2 . $key;
                  }
                  else {
                    $commArray[] = 2 . $value;
                  }
                }
              }
            }
          }
        }
      }
    }
    
    // Call Print Table passing the filled catecogies array 
    printTable($backArray, $geoArray, $socArray, $enerArray, $commArray);
  }
  
  
